#!/usr/bin/env bash

echo "Broadcasting..."
TX='{"mode":"block","tx":""}'
curl -X POST localhost:1317/txs --data-binary "$TX"

echo ""

echo "Now we can query the DID..."
curl -X GET localhost:1317/did/did:ixo:4XJLBfGtWSGKSz4BeRxdun

